package security;


import com.eaglebank.api.security.JwtService;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.junit.jupiter.MockitoExtension;
import org.springframework.security.core.userdetails.User;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.test.util.ReflectionTestUtils;

import java.util.Collections;

import static org.assertj.core.api.Assertions.assertThat;

@ExtendWith(MockitoExtension.class)
class JwtServiceTest {

    @InjectMocks
    private JwtService jwtService;

    private UserDetails userDetails;

    @BeforeEach
    void setUp() {
        String secret = "";
        ReflectionTestUtils.setField(jwtService, "jwtSecret", secret);

        userDetails = new User("test@example.com", "password", Collections.emptyList());
    }

    @Test
    void generateToken_and_extractUsername_shouldWork() {
        // Act
        String token = jwtService.generateToken(userDetails);
        String extractedUsername = jwtService.extractUsername(token);

        // Assert
        assertThat(token).isNotNull();
        assertThat(extractedUsername).isEqualTo("test@example.com");
    }

    @Test
    void isTokenValid_shouldReturnTrue_forValidToken() {
        // Act
        String token = jwtService.generateToken(userDetails);
        boolean isValid = jwtService.isTokenValid(token, userDetails);

        // Assert
        assertThat(isValid).isTrue();
    }

    @Test
    void isTokenValid_shouldReturnFalse_forDifferentUser() {
        // Arrange
        UserDetails otherUserDetails = new User("other@example.com", "password", Collections.emptyList());

        // Act
        String token = jwtService.generateToken(userDetails);
        boolean isValid = jwtService.isTokenValid(token, otherUserDetails);

        // Assert
        assertThat(isValid).isFalse();
    }
}